#include <stdint.h>
#include "my_typedef.h"
#include "iodefine.h"
#include "app.h"
#include "run.h"
#include "init.h"
#include "intrpt.h"
#include "serial.h"
#include "sensor.h"
#include "global.h"
#include "common.h"
#include "parameter.h"
#include "map.h"
#include "contest.h"
#include "adjustment.h"

void main(void) {
	init();
	checkBatt();

	switch (selectMode(10)) {
	case 0: //////////////////////////////////////////////////////////////////////////////////////
		driveRGB(ORANGE, ON);
		//selectContest();
		waitSensor();
		waitTime(1000);
		calcGyroZRef();
		g_current_angle = 0;
		g_current_angularvelo = 0;
		g_log_count = 0;
		waitTime(1500);
		while (1) {
			myprintf("%f	%f	%f\n", g_current_angle, g_current_angularvelo,
					g_gyro_reference);

		}
		soundSensor();
		printLog();
		break;
	case 1: //////////////////////////////////////////////////////////////////////////////////////
		driveRGB(MAGENTA, ON);
		selectAdjustment();
		break;
	case 2: //////////////////////////////////////////////////////////////////////////////////////
		driveRGB(BLUE, ON);
		waitSensor();

		driveRGB(GREEN, ON);
		waitTime(1000);
		initRun();
		driveMotor(ON);
		runStraight(5, HALF_SECTION, 0.5, 0.5);
		while (1) {

			runStraight(5, SECTION, 0.5, 0.5);
			turnCorner(turn_90_L);
		}

		break;
	case 3: //////////////////////////////////////////////////////////////////////////////////////
		driveRGB(YELLOW, ON);
		waitSensor();

		driveRGB(GREEN, ON);
		waitTime(1000);
		initRun();
		driveMotor(ON);
		runStraight(5, HALF_SECTION, 0.5, 0.5);
		while (1) {

			runStraight(5, SECTION, 0.5, 0.5);
			turnCorner(turn_90_R);
		}
		break;
	case 4: //////////////////////////////////////////////////////////////////////////////////////
		driveRGB(CYAN, ON);
		waitSensor();

		driveRGB(GREEN, ON);
		waitTime(1000);
		calcGyroZRef();
		driveSuction(70, ON);
		waitTime(1000);
		initRun();
		driveMotor(ON);
		switchSensorLED(ON);
		runStraight(25, SECTION * 2, 1.8, 1.8);

		turnCorner(turn_90_wide_R_1500);
		switchSensorLED(OFF);
		runStraight(25, SECTION * 2, 1.8, 0);
		driveMotor(OFF);
		driveSuction(70, OFF);
		waitButton();
		printLog();
		break;
	case 5: //////////////////////////////////////////////////////////////////////////////////////
		driveRGB(MAGENTA, ON);

		waitSensor();

		driveRGB(GREEN, ON);
		waitTime(1000);

		calcGyroZRef();
		initRun();
		driveMotor(ON);
		switchSensorLED(ON);
		g_flag_diagonal = 1;
		runStraight(5, SECTION * M_SQRT2 * 3, 0.5, 0.5);
		//switchSensorLED(ON);
		//runBlindAlley(0.5);
		//runStraight(5, SECTION, 0.5, 0);
		driveMotor(OFF);
		driveSuction(70, OFF);
		waitButton();
		printLog();

		break;
	case 6: //////////////////////////////////////////////////////////////////////////////////////
		driveRGB(WHITE, ON);

		waitSensor();
		waitTime(100);
		calcGyroZRef();
		driveRGB(GREEN, ON);
		waitTime(1000);
		initRun();
		driveMotor(ON);

		//while (1) {
		turnCorner(pivot_90_L);
		waitTime(200);
		//}
		driveMotor(OFF);
		waitButton();
		printLog();
		break;
	case 7: //////////////////////////////////////////////////////////////////////////////////////
		driveRGB(ORANGE, ON);
		waitSensor();
		switchSensorLED(ON);

		driveRGB(GREEN, ON);
		waitTime(1000);
		calcGyroZRef();
		soundStartRun();
		driveSuction(70, OFF);
		waitTime(1000);
		initRun();
		driveMotor(ON);
		runStraight(5, SECTION * 4, 0.5, 0);
		switchSensorLED(ON);
		driveMotor(OFF);
		driveSuction(70, OFF);
		driveSuction(100, OFF);

		waitButton();
		printLog();
		break;
	case 8: //////////////////////////////////////////////////////////////////////////////////////
		driveRGB(WATER, ON);
		switchSensorLED(ON);
		while (1) {

			myprintf("FL:%6.1d L:%6.1d R:%6.1d FR:%6.1d\n", g_sensor_FL,
					g_sensor_L, g_sensor_R, g_sensor_FR);
			waitTime(50);
		}
		break;
	case 9: //////////////////////////////////////////////////////////////////////////////////////
		driveRGB(LBLUE, ON);
		//driveSuction(100, ON);
		waitSensor();
		waitTime(1000);
		calcGyroZRef();
		g_flag_circuit = 1;

		driveSuction(70, ON);
		driveMotor(ON);
		waitTime(1000);
		switchSensorLED(ON);
		initRun();
		runStraight(25, SECTION * 7, 4.5, 0);
		driveSuction(70, OFF);
		driveMotor(OFF);
		waitButton();
		printLog();

		break;
	case 10: //////////////////////////////////////////////////////////////////////////////////////
		driveRGB(LRED, ON);
		waitSensor();

		calcGyroZRef();
		switchSensorLED(ON);
		g_flag_control = 1;
		g_flag_blindalley_dis = 1;
		driveMotor(ON);
		while (1) {

		}
		break;
	default: //////////////////////////////////////////////////////////////////////////////////////
		break;
	}

}

