#include <stdint.h>
#include "my_typedef.h"
#include "iodefine.h"
#include "app.h"
#include "run.h"
#include "init.h"
#include "intrpt.h"
#include "serial.h"
#include "sensor.h"
#include "global.h"
#include "common.h"
#include "parameter.h"
#include "map.h"

void main(void) {
	init();
	checkBatt();

	switch (selectMode(10)) {
	case 0: //////////////////////////////////////////////////////////////////////////////////////
		driveRGB(ORANGE, ON);
		switchSensorLED(ON);
		waitSensor();
		driveRGB(CYAN, ON);
		driveMotor(ON);
		soundCountdown();
		calcGyroZRef();
		g_flag_control = 1;
		g_log_count = 0;
		g_distance = 0;

		searchFurukawa();
		switchSensorLED(OFF);
		driveMotor(OFF);
		g_current_x = START_X;
		g_current_y = START_Y;
		g_orient = 0x01;
		countStepShortest();
		makePath();
		makePath2();
		makePath3();

		while (1) {
			driveRGB(BLUE, ON);
			waitButton();
			switchSensorLED(ON);
			waitSensor();
			driveRGB(GREEN, ON);
			soundCountdown();
			driveSuction(70, ON);
			waitTime(1000);
			g_flag_control = 1;
			g_log_count = 0;
			g_distance = 0;
			g_target_velo=0;
			driveMotor(ON);
			runPathDiagonal();
			switchSensorLED(ON);
			driveMotor(OFF);
			driveSuction(100, OFF);
		}
		break;
	case 1: //////////////////////////////////////////////////////////////////////////////////////
		driveRGB(GREEN, ON);

		break;
	case 2: //////////////////////////////////////////////////////////////////////////////////////
		driveRGB(BLUE, ON);

		waitTime(3000);
		driveRGB(GREEN, ON);
		calcGyroZRef();

		while (1) {

			myprintf("%f %f\n", g_current_angularvelo, g_angle);
		}
		break;
	case 3: //////////////////////////////////////////////////////////////////////////////////////
		driveRGB(YELLOW, ON);
		switchSensorLED(ON);
		driveMotor(ON);
		waitTime(2000);
		calcGyroZRef();

		g_flag_control = 1;
		g_log_count = 0;
		g_distance = 0;
		runStraight(5, SECTION * 2 + HALF_SECTION, 1.3, 1);

		turnCorner(turn_135_out_R_1000);

		runStraight(5, SECTION * 2 + HALF_SECTION, 1.3, 0);
		switchSensorLED(ON);
		driveMotor(OFF);
		waitButton();
		printLog();
		break;
	case 4: //////////////////////////////////////////////////////////////////////////////////////
		driveRGB(CYAN, ON);
		while (1) {
			myprintf("%f\n", returnGyroZVal());
			waitTime(50);
		}
		break;
	case 5: //////////////////////////////////////////////////////////////////////////////////////
		driveRGB(MAGENTA, ON);
		driveMotor(ON);
		switchSensorLED(ON);
		waitTime(2000);
		calcGyroZRef();
		g_flag_control = 1;
		g_log_count = 0;
		g_distance = 0;
		runStraight(5, SECTION * 5, 3, 0);
		waitTime(1000);
		driveMotor(OFF);
		waitButton();
		printLog();
		break;
	case 6: //////////////////////////////////////////////////////////////////////////////////////
		driveRGB(WHITE, ON);

		break;
	case 7: //////////////////////////////////////////////////////////////////////////////////////
		driveRGB(ORANGE, ON);

		break;
	case 8: //////////////////////////////////////////////////////////////////////////////////////
		driveRGB(WATER, ON);
		switchSensorLED(ON);
		while (1) {

			myprintf("FL:%6.1d L:%6.1d R:%6.1d FR:%6.1d\n", g_sensor_FL,
					g_sensor_L, g_sensor_R, g_sensor_FR);
			waitTime(50);
		}
		break;
	case 9: //////////////////////////////////////////////////////////////////////////////////////
		driveRGB(LBLUE, ON);
		driveSuction(100, ON);
		break;
	case 10: //////////////////////////////////////////////////////////////////////////////////////
		driveRGB(LRED, ON);
		calcGyroZRef();
		g_flag_control = 1;
		driveMotor(ON);
		while (1) {

		}
		break;
	default: //////////////////////////////////////////////////////////////////////////////////////
		break;
	}

}
